mapper.update:
UPDATE tbl_diary
SET user_id = #{userId}, title = #{title}, content = #{content}, datetime = #{datetime}
WHERE id = #{id}

mapper.select:
		SELECT id, user_id, title, content, datetime
		FROM tbl_diary
        WHERE user_id = #{userId} and datetime = #{datetime}

		SELECT id, title, content, datetime
		FROM tbl_diary
        WHERE id = #{id}
