mapper.update:
UPDATE tbl_diary
SET title = #{title}, content = #{content}
WHERE id = #{id}

mapper.select:
SELECT id, user_id, title, content, datetime FROM tbl_diary
WHERE user_id = #{userId} and (datetime between #{start_date} and #{end_date}) order by datetime desc
WHERE user_id = #{userId} and datetime = #{start_date}

		SELECT id, user_id, title, content, datetime
		FROM tbl_diary
        WHERE id = #{id}
