mapper.select:
SELECT id, user_id, pet_id, type, type, title, memo, datetime
FROM tbl_schedule
WHERE user_id = #{userId}

SELECT id, user_id, pet_id, type, title, memo, datetime
FROM tbl_schedule
WHERE pet_id = #{petId}

mapper.update:
update tbl_schedule
set type = #{type}, title=#{title}, memo=#{memo}, datetime=#{datetime}
where id = #{id}
