mapper.select:
SELECT id, email, password, nickname, profile_image, social_type
FROM tbl_user
WHERE id = #{userid}

mapper.update:
update tbl_user
set email = #{email}, password = #{password}, nickname = #{nickname}, profile_image=#{profileImage}
where id = #{id}

update tbl_user
set is_withdrawal = 1
where id = #{userid}

UPDATE tbl_user
SET password = #{password}
WHERE email = #{email} AND is_withdrawal = 0
