mapper.select:
select id, email, password, nickname, profile_image
from user
where id = #{userid}

mapper.update:
update user
set email = #{email}, password = #{password}, nickname = #{nickname}, profile_image=#{profile_image}
where id = #{userid}

mapper.select:
select email
from user
where email=#{email}

select nickname
from user
where nickname=#{nickname}

select id, email, nickname, profile_image
from user
where email = #{email} and password = #{password}

mapper.update:
update user
set password = #{password}
where id = #{userid}
