mapper.update:
update tbl_user
set nickname = #{nickname}, profile_image=#{profileImage}
where id = #{id}

update tbl_user
set is_withdrawal = 1
where id = #{userid}

UPDATE tbl_user
SET password = #{password}
WHERE email = #{email} AND is_withdrawal = 0

UPDATE tbl_user
SET device_token = #{deviceToken}
WHERE id = #{id} AND is_withdrawal = 0
